import { REGISTER } from "../constants/action-types";

const initialState = {
  loggedIn: false,
  currentUser: null,
  };

let user = JSON.parse(localStorage.getItem('user'));

export const rootReducer = (state = initialState, action) => {
  if (action.type === REGISTER) {
    return {
      loggingIn: true,
      user: action.user
    } 
}
  return state;
};
