import React, { Component } from 'react';
import { connect } from "react-redux";
import '../../App.css';
import BoardContainer from './BoardContainer/BoardContainer';
import UserContainer from './UserContainer/UserContainer';
import { handleRegister, handleLogin } from '../actions/actions'

const mapDispatchToProps = (dispatch) => {
  return {
      handleRegister: user => dispatch(handleRegister(user)),
      handleLogin: user => dispatch(handleLogin(user)),
  }
};

class ConnectedApp extends Component {
  render(){
    return (
      <div className="App">
      <div className="header">
        <h4>a box of thoughts</h4>
      </div>
        <div>
          <button onClick={ this.logout }>Logout</button>
          {
            this.loggedIn ?
            <BoardContainer showBoards={ this.state.currentUser.boards } />
            :
            <UserContainer />
          }
        </div>
      </div>
    );
  }
}

const App = connect(null, mapDispatchToProps)(ConnectedApp);

export default App;
