import React, { Component } from 'react';
import Search from './Search';
import Axios from 'axios';


class MakeBoard extends Component {
    constructor(){
        super();
        this.state = {
            title: "",
            description: "",
            search: "",
            results: [],
            images: [],
            currentPage: 1        }
    };
    handleChange = (e) => {
        this.setState({
            [e.target.name] : e.target.value
        })
    };
    handleSubmit = (e) => {
        // e.preventDefault;
        // e.target.reset();
        this.setState({
            name: null,
            description: null
        })
        this.props.createBoard(this.state);
    };
// search crap
searchImages = () => {
    Axios({
      method: 'get',
      url: 'https://api.unsplash.com/search/photos',
      params: {
        client_id: '1fe232c10d045efb942c686798a897086057edb740c100d8ee47adf69d77c998', 
        query: this.state.search,
        per_page: 9,
        page: this.state.currentPage,
      }
    })
    .then(reponse => {
        // console.log(reponse); WORKING
        this.setState({results: reponse.data});
      })
    .then(showResults => {
        this.handleSearchResults();
    })
    .catch(err => {
        console.log(err);
    })
};
handleSearchResults = () => {
    const searchResultsArray = this.state.results.results;
    // console.log(searchResultsArray) WORKING
    const imageSearchList = searchResultsArray.map((result) => {
        this.setState({
            images: [...this.state.images, result.urls.regular]
        })
    })
    console.log(this.state.images, 'this.state.images MakeBoard')
};
handleSearchSubmit = (e) => {
    e.preventDefault();
    this.searchImages(this.state);
};
// end of search crap



    // refactor for this component
    createImage = async (query) => {
        const newImage = await fetch(`https://api.unsplash.com/search/photos/?page=1&per_page=10&query=${query}&client_id=1fe232c10d045efb942c686798a897086057edb740c100d8ee47adf69d77c998`, {
            credentials: 'include',
            method: "POST",
            body: JSON.stringify(query),
            headers: {
                "Content-Type": "application/json"
            }
        })
        const parsedResponse = await newImage.json();
        if(newImage.status === 200){
            this.setState({
                images: [...this.state.boards.image, parsedResponse]
            })
        }
        console.log(this.state.boards, 'this.state.boards')
        console.log(this.state.boards.images, 'this.state.boards.images')

    };
    render(){
        return (
            <div>
                <h1>Create a New Board</h1>
                <form onSubmit={ this.handleSubmit }>
                    <div>
                        *Title: <input onChange={ this.handleChange } type="text" name="title" />
                    </div>
                    <div>
                        Description: <input onChange={ this.handleChange } type="text" name="description" />
                    </div>
                    <div>
                        <input type="submit" />
                    </div>
                    <div>
                        <small>* required</small>
                    </div>
                </form>
                    <div>
                        <Search handleChange={ this.handleChange } handleSearchResults={ this.handleSearchResults } />
                    </div>
            </div>

        )
    }

}

export default MakeBoard;
