import React, { Component } from 'react';

class Search extends Component {
    constructor(){
        super();
        this.state = {
            search: "",
            results: [],
            // currentPage: 1
        }
    }

    searchGifs = async () => {
        const foundImages = await fetch(`https://api.unsplash.com/search/photos`, {
            method: 'GET',
            credentials: 'include',
            client_id: '1fe232c10d045efb942c686798a897086057edb740c100d8ee47adf69d77c998',
            query: this.state.search,
            per_page: 9,
            // page: this.state.currentPage

        });
        const parsedImages = await foundImages.json();

        const imagesArray = parsedImages.data;
        console.log(imagesArray, 'imagesArray from SEARCH')
        const imagesShow = imagesArray.map((image) => {
            return(
                <div className="images">
                    {/* <img src={  } /> */}
                </div>
            )
        })
        this.setState({
            results: imagesShow
        })
    };
    handleChange = (e) => {
        this.setState({
            [e.target.name] : e.target.value
        })
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.searchImages(this.state);
    };
    render(){
        return (
            <div>
                <h1>Search</h1>
                <form onSubmit={ this.handleSubmit }>
                    <h3>search for images: <input onChange={ this.handleChange } type="text" name="search" placeholder="when in doubt, search for cats"/>
                    <button type="submit">SUBMIT</button></h3>
                </form>
                { this.state.results }
            </div>
        )
    };
}

export default Search;
