import React, { Component } from 'react';
import $ from 'jquery';
import { Button } from 'reactstrap';

class SearchResults extends Component {
    // componentDidMount(){
    //     $('img').click(function(){
    //         $('.selected').removeClass('selected'); 
    //         $(this).addClass('selected'); 
    //      });
    // }
    handleClick(){
        $( "img.option" ).click(function() {
            $( this ).toggleClass( "highlight" );
          });
        // $("img").click(function() {
        //     $(this).toggleClass("selected");
        //     // $(this).toggleClass("uncheckedborder");
        // });  

    }

    render(){
        const searchResultsList = this.props.images.map((image, i) => {
            return(
                <div className="image-options" key={i} onClick={this.handleClick}>
                    <img class="option" onClick={this.handleClick} alt="" src={ image } id={i}/>

                    {/* <img class="" onClick={ () => this.props.handleImageClick(image) } alt="" src={ image } id={i}/> */}
                    {/* <img class="fb_thumbnail uncheckedborder" onClick={ () => this.props.handleImageClick(image) } alt="" src={ image } id={i}/> */}
                </div>
            )
        })
        return(
            <div>
            { searchResultsList }
            </div>
        )
    }
}

export default SearchResults;
