import { REGISTER, LOGIN, LOGOUT, GET_USER, CREATE_BOARD } from "../constants/action-types";

const initialState = {
  loggedIn: false,
  currentUser: null,
  boards: [],
  renderBoardDetail: false,
  };


export const rootReducer = (state = initialState, action) => {
  if (action.type === REGISTER) {
    return {
      loggedIn: true,
      currentUser: action.payload.data
    } 
  }
  if (action.type === LOGIN) {
    return {
      loggedIn: true,
      currentUser: action.payload.data
    } 
  }
  if (action.type === LOGOUT) {
    return {
      loggedIn: false,
      currentUser: null
    } 
  }
  if (action.type === GET_USER) {
    return {
      loggedIn: state.loggedIn,
      currentUser: state.currentUser,
      boards: action.payload.data,
      renderBoardDetail: true
    } 
  }
  if (action.type === CREATE_BOARD) {
    return {
      loggedIn: state.loggedIn,
      currentUser: state.currentUser,
      boards: state.boards.concat(action.payload.data),
      renderBoardDetail: state.renderBoardDetail,
    } 
  }
  return state;
};
