import React, { Component } from 'react';
import Search from './Search';

class MakeBoard extends Component {
    constructor(){
        super();
        this.state = {
            title: "",
            description: "",
            images: [],
            imageSearchTerm: ""
        }
    };
    handleChange = (e) => {
        this.setState({
            [e.target.name] : e.target.value
        })
    };
    handleSubmit = (e) => {
        // e.preventDefault;
        // e.target.reset();
        this.setState({
            name: null,
            description: null
        })
        this.props.createBoard(this.state);
    };
    handleSearchSubmit = (e) => {
        this.setState({
            images: [...this.state.images, this.state.imageSearchTerm]
        })
    };
    imageStateChange = (newState) => {
        console.log('working!!!!!!!!!!!')
        this.setState({
            images: newState.images
        })
    }
    render(){
        console.log(this.state.images, 'this.state.images from Make Board')
        return (
            <div>
                <h1>Create a New Board</h1>
                <form onSubmit={ this.handleSubmit }>
                    <div>
                        *Title: <input onChange={ this.handleChange } type="text" name="title" />
                    </div>
                    <div>
                        Description: <input onChange={ this.handleChange } type="text" name="description" />
                    </div>
                    <div>
                        <input type="submit" />
                    </div>
                    <div>
                        <small>* required</small>
                    </div>
                </form>
                    <div>
                        <Search imageStateChange={ this.imageStateChange } />
                    </div>
            </div>

        )
    }

}

export default MakeBoard;
