class App extends Component {
  constructor(){
    super();
    this.state = {
      loggedIn: false,
      currentUser: null,
      allBoards: []
    }
  }
  handleRegister = async (formData) => {
    try{
      console.log(formData, 'register');
      const newUser = await fetch("http://localhost:9000/users", {
        method: "POST",
        body: JSON.stringify(formData),
        credentials: 'include',
        headers: {
          "Content-Type": "application/json",
        }
      })
      const parsedResponse = await newUser.json();
      if(parsedResponse.status === 200){
        this.setState({
          loggedIn: true,
          currentUser: parsedResponse.data
        })
      } 

    }catch(err){
      console.log(err);
    }
  };
  handleEditProfile = async (formData) => {
    try{
      console.log('logout');

    }catch(err){
      console.log(err); 
    }
  }
  handleLogin = async (formData) => {
    try{
      const loginUser = await fetch('http://localhost:9000/users/login', {
        method: "POST",
        body: JSON.stringify(formData),
        credentials: 'include',
        headers: {
          "Content-Type": "application/json"
        }
      })
      const parsedLoginResponse = await loginUser.json();
      console.log(parsedLoginResponse, 'login')
      if(parsedLoginResponse.status === 200){
        this.setState({
          loggedIn: true,
          currentUser: parsedLoginResponse.data,
        })
      }
    }catch(err){
      console.log(err);
    }
  };
  // NOT WORKING
  logout = async () => {
    console.log('logout');
    try{
      const logoutUser = await fetch('http://localhost:9000/users/logout', {
        method: "GET",
        credentials: 'include'
      });
      if(logoutUser.status === 200){
        this.setState({
          loggedIn: false,
          currentUser: null
        })
      }
    }catch(err){
      console.log(err);
    }

  };
  allBoardsStateChange = (newState) => {
    this.setState({
      allBoards: newState.boards
    })
  };
  render(){
    console.log(this.state.currentUser, 'current user')
    return (
      <div className="App">
      <div className="header">
        <h4>a box of thoughts</h4>
      </div>
        <div>
          {
            this.state.loggedIn ?
            <BoardContainer showBoards={ this.state.currentUser.boards } />
            :
            <UserContainer handleRegister={ this.handleRegister } handleLogin={ this.handleLogin } handleEditProfile={ this.handleEditProfile } />
          }
        </div>
      </div>
    );
  }
}

export default App;
