import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class EditBoard extends Component {
    constructor(){
        super();
        this.state = {
            title: "",
            description: "",
        }
    };
    componentDidUpdate(){
            // this.findBoard(this.props.editBoardId)
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name] : e.target.value,
        })
    };

    findBoard = async (foundBoard) => {
        const boardInfo = await fetch(`${process.env.REACT_APP_BACKEND_ADDRESS}/boards/${foundBoard}`, {
            method: "GET",
            body: JSON.stringify(foundBoard),
            // headers: {
            //     "Content-Type": "application/json"
            // }
        })
        console.log(boardInfo)
        this.setState({
            title: foundBoard.title,
            description: foundBoard.description
        })
    };

    render(){
        console.log(this.props.editBoardId, 'props1')
        console.log(this.props, 'props2')
        console.log(this.state, 'state')

        if(this.props.editModal){
            this.findBoard(this.props.editBoardId)
        }

        return (
            <div>
                <Modal isOpen={ this.props.editModal } toggle={ this.props.toggleEdit }>
                <ModalHeader toggle={ this.props.toggleEdit }>Edit Board Details</ModalHeader>
                <ModalBody>
                    <div>
                        *Title: <input onChange={ this.handleChange } type="text" name="title" value={ this.state.title }/>
                    </div>
                    <div>
                        Description: <input onChange={ this.handleChange } type="text" name="description" value={ this.state.description } />
                    </div>
                    <div>
                        <small>* required</small>
                    </div>
                </ModalBody>
                <ModalFooter>
                    <Button color="secondary" onClick={ () => this.props.handleEditSubmit(this.state) }>Submit</Button>{' '}
                    <Button outline color="secondary" onClick={ this.props.toggleEdit }>Cancel</Button>
                </ModalFooter>
                </Modal>
            </div>
        )
    };
}

export default connect()(EditBoard);
