import React, { Component } from 'react';
import { connect } from "react-redux";
import '../../App.css';
import { Button } from 'reactstrap';
import BoardContainer from './BoardContainer/BoardContainer';
import UserContainer from './UserContainer/UserContainer';
import { handleRegister, handleLogin, logout } from '../actions/actions'

const mapDispatchToProps = (dispatch) => {
  return {
      handleRegister: user => dispatch(handleRegister(user)),
      handleLogin: user => dispatch(handleLogin(user)),
      logout: user => dispatch(logout(user)),
  }
};

const mapStateToProps = state => {
  return { loggedIn: state.loggedIn, currentUser: state.currentUser };
};

class ConnectedApp extends Component {
  render(){
    return (
      <div className="App">
        {
          this.props.loggedIn ?
          <div>
            <div className="header">
              <h4>a box of thoughts</h4>
            </div>

            <Button className="navi" outline color="secondary">Create Board</Button>
            <Button className="navi" outline color="secondary" onClick={ this.props.logout }>Logout</Button>

            <BoardContainer />
            </div>
            :
            <div>
            <div className="header">
              <h4>a box of thoughts</h4>
            </div> 
            <UserContainer />
            </div>
        }
        </div>
    );
  }
}

const App = connect(mapStateToProps, mapDispatchToProps)(ConnectedApp);

export default App;
