import React, { Component } from 'react';
import Axios from 'axios';

class Search extends Component {
    constructor(){
        super();
        this.state = {
            search: "",
            results: [],
            images: [],
            currentPage: 1
        }
    };
    searchImages = () => {
        Axios({
          method: 'get',
          url: 'https://api.unsplash.com/search/photos',
          params: {
            client_id: '1fe232c10d045efb942c686798a897086057edb740c100d8ee47adf69d77c998', 
            query: this.state.search,
            per_page: 9,
            page: this.state.currentPage,
          }
        })
        .then(reponse => {
            console.log(reponse);
            this.setState({results: reponse.data});
          })
          .catch(err => {
            console.log(err);
          })
        }

    //     .then(reponse => {
    //         this.setState({[this.state.results]: response.data.map((result) => {
    //             return(
    //                 result.urls.regular
    //             )
    //         })
    //     })
    //     .catch(err => {
    //       console.log(err);
    //     })
    // };

    handleChange = (e) => {
        this.setState({
            [e.target.name] : e.target.value
        })
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.searchImages(this.state);
    };
    render(){
        console.log(this.state.results, 'this.state.results')
        console.log(this.state.results.results, 'this.state.results.results')

        // const imageSearchList = this.state.results.results.map((result) => {
