import { REGISTER, LOGIN } from "../constants/action-types";

const initialState = {
  loggedIn: false,
  currentUser: null,
  };


export const rootReducer = (state = initialState, action) => {
  if (action.type === REGISTER) {
    return {
      loggedIn: true,
      user: action.payload.data
    } 
  }
  if (action.type === LOGIN) {
    return {
      loggedIn: true,
      user: action.payload.data
    } 
  }
  return state;
};
