        // console.log(imagesList)

        const boardsList = this.props.boards.map((board) => {
            return (
                <div key={ board._id } className="parent">
                    <div>{ board.title }</div>
                    <div>{ board.description }</div>
                    { imagesList }
                    <ImageList images = {board.images}/>
                </div>
            )
        });   
        return(
            <div>
                <h1>BoardDetail</h1>
                <div>
                    { boardsList }
                    <div>
                    </div>
                </div>
            </div>
        )
    }
}

export default BoardDetail;
