import React, { Component } from 'react';

class BoardDetail extends Component {
    constructor(){
        super();
        this.state = {
            // boards: [this.props]
        }
    }
    render(){
        console.log(this.props.boards, 'this.props.boards');

        const imageList = this.props.boards.map((image, i) => {
            return (
                image.data[i].images
            )
        });
        console.log(imageList);
        const images = imageList.map((image, i) => {
            console.log(image, 'image from images')
            return(
                image[i]
            )
        });
        const imagesShow = images.map((image, i) => {
            return(
                <img alt={i} src={ image } />
            )
        });
        const boardsList = this.props.boards.map((board, i) => {
            return (
                <div key={ board.data[i]._id }>
                    <div>{ board.data[i].title }</div>
                    <div>{ board.data[i].description }</div>
                    <div>{ imagesShow } </div>
                </div>
            )
        });   
        return(
            <div>
                <h1>BoardDetail</h1>
                <div>
                    { boardsList }
                </div>
            </div>
        )
    }
}

export default BoardDetail;
