import React, { Component } from 'react';

class Search extends Component {
    constructor(){
        super();
        this.state = {
            search: "",
            results: [],
            currentPage: 1
        }
        // this.client_id = '1fe232c10d045efb942c686798a897086057edb740c100d8ee47adf69d77c998'

    }

    // searchImages = async () => {
