    //             </div>
    //         )
    //     })
    //     this.setState({
    //         results: imagesShow
    //     })
    // };
    searchImages = () => {
        Axios({
          method: 'get',
          url: 'https://api.unsplash.com/search/photos',
          params: {
            client_id: '1fe232c10d045efb942c686798a897086057edb740c100d8ee47adf69d77c998', //2.hesap 
            query: this.state.query,
            per_page: 15,
            page: this.state.currentPage,
          }
        })
        .then(reponse => {
          console.log(reponse);
          this.setState({results: reponse.data});
        })
        .catch(err => {
          console.log(err);
        })
      }
    handleChange = (e) => {
        this.setState({
            [e.target.name] : e.target.value
        })
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.searchImages(this.state);
    };
    render(){
        return (
            <div>
                <h1>Search</h1>
                <form onSubmit={ this.handleSubmit }>
                    <h3>search for images: <input onChange={ this.handleChange } type="text" name="search" placeholder="when in doubt, search for cats"/>
                    <button type="submit">SUBMIT</button></h3>
                </form>
                { this.state.results }
            </div>
        )
    };
}

export default Search;
