            console.log(image.images, 'image.images')
            image.images.map((src) => {
                console.log(src)
                // return <img key={image} src={require(`./icons/${image}.png`)} alt="" className="img-responsive" />

            })
        })
        console.log(imagesList)

        const boardsList = this.props.boards.map((board) => {
            return (
                <div key={ board._id }>
                    <div>{ board.title }</div>
                    <div>{ board.description }</div>
                    {/* <ImageDetail imagesList={ this.imagesList } /> */}
                </div>
            )
        });   
        return(
            <div>
                <h1>BoardDetail</h1>
                <div>
                    { boardsList }
                    <div>
                    <img src="https://images.unsplash.com/photo-1557411197-336ed936e9fa?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1088&q=80" />
                    </div>
                </div>
            </div>
        )
    }
}

export default BoardDetail;
