import React, { Component } from 'react';
import ImageList from './ImageList';
import MakeBoard from '../MakeBoard/MakeBoard';

class BoardDetail extends Component {
    constructor(){
        super();
        this.state = {
            id: ""
        }
    };
    editBoardButtonClick = (e, id) => {
        <MakeBoard />
        // this.state.boards.map((board) => {
        //     if (board._id === e.target.id){
        //         this.editBoard(board)
        //     }
        // })
    };

    editBoard = async (board) => {
        const response = await fetch(`http://localhost:9000/boards/${board._id}`, {
            method: "PUT",
            body: JSON.stringify(board),
            headers: {
                "Content-Type": "application/json"
            }
        })
        if(response.status === 200){
            this.setState({
                boards: [...this.state.boards, board]
            })
        }
    }; 

    render(){
        const boardsList = this.props.boards.map((board, i) => {
            return (
                <div key={ board._id } id={ board._id } className="parent">
                    <hr />
                    <div>{ board.title }</div>
                    <div>{ board.description }</div>
                    <button onClick={ this.props.addNewImageButtonClick } id={ board._id }>Add New Image</button>
                    <button id={ board._id } onClick={ this.editBoardButtonClick }>Edit Board</button>
                    <button id={ board._id } onClick={ this.props.deleteBoardButtonClick }>Delete Board</button>
                    <ImageList images = { board.images } deleteImageButtonClick= { this.props.deleteImageButtonClick } board = {board} />
                </div>
            )
        });   
        return(
            <div>
                <h1>Existing Boards</h1>
                <div>
                    { boardsList }
                    <div>
                    </div>
                </div>
            </div>
        )
    }
}

export default BoardDetail;
