import React, { Component } from 'react';
import ImageDetail from './ImageDetail';

class BoardDetail extends Component {
    constructor(){
        super();
        this.state = {
            images: []
        }
    }
    render(){
        // TAKE THIS AND MAKE IT THE IMG SRC
        const imagesList = this.props.boards.map((image) => {
            console.log(image.images, 'image.images')
            image.images.map((src) => {
                return <img src= {src} />
            })
        })
        console.log(imagesList)

        const boardsList = this.props.boards.map((board) => {
            return (
                <div key={ board._id }>
                    <div>{ board.title }</div>
                    <div>{ board.description }</div>
                    {/* <ImageDetail imagesList={ this.imagesList } /> */}
                </div>
            )
        });   
        return(
            <div>
                <h1>BoardDetail</h1>
                <div>
                    { boardsList }
                </div>
            </div>
        )
    }
}

export default BoardDetail;
